mapper.insert:
insert into tb_cmpnyhist (
	cmpnyhist_sn
	, lang_code
	, year
	, mt
	, cn
	, regist_dt
	, register_sn
	, updt_dt
	, updusr_sn
) values (
	#{cmpnyhistSn}
	, #{langCode}
	, #{year}
	, #{mt}
	, #{cn}
	, now()
	, #{registerSn}
	, now()
	, #{updusrSn}
)

mapper.update:
update tb_cmpnyhist
set
    updt_dt = now()
    , updusr_sn = #{updusrSn}
	, lang_code = #{langCode} 
	, year = #{year} 
	, mt = #{mt} 
	, cn = #{cn} 
where cmpnyhist_sn = #{cmpnyhistSn}
