mapper.insert:
insert into tb_empa (
	empa_sn
	, lang_code
	, empa_sj
	, rcept_begin_de
	, rcept_end_de
	, rcept_sttus_code
	, sport_se_code
	, atch_id
	, empa_cn
	, regist_dt
	, register_sn
	, updt_dt
	, updusr_sn
) values (
	#{empaSn}
	, #{langCode}
	, #{empaSj}
	, #{rceptBeginDe}
	, #{rceptEndDe}
	, #{rceptSttusCode}
	, #{sportSeCode}
	, #{atchId}
	, #{empaCn}
	, now()
	, #{registerSn}
	, now()
	, #{updusrSn}
)

mapper.update:
update tb_empa
set
    updt_dt = now()
    , updusr_sn = #{updusrSn}
	, lang_code = #{langCode} 
	, empa_sj = #{empaSj} 
	, rcept_begin_de = #{rceptBeginDe} 
	, rcept_end_de = #{rceptEndDe} 
	, rcept_sttus_code = #{rceptSttusCode} 
	, sport_se_code = #{sportSeCode} 
	, atch_id = #{atchId} 
	, empa_cn = #{empaCn} 
where empa_sn = #{empaSn}
