mapper.insert:
insert into tb_main_ntt (
	main_ntt_sn
	, lang_code
	, main_bbs_id
	, main_ntt_sj_pc
	, main_ntt_sj_mo
	, main_ntt_cn_pc
	, main_ntt_cn_mo
	, image_atch_id_pc
	, image_atch_id_mo
	, media_url_pc
	, media_url_mo
	, expsr_yn
	, expsr_prty
	, title
	, bt_yn
	, bt_sj_pc_1
	, bt_sj_mo_1
	, link_url_1
	, bt_sj_pc_2
	, bt_sj_mo_2
	, link_url_2
	, regist_dt
	, register_sn
	, updt_dt
	, updusr_sn
) values (
	#{mainNttSn}
	,#{langCode}
	, #{mainBbsId}
	, #{mainNttSjPc}
	, #{mainNttSjMo}
	, #{mainNttCnPc}
	, #{mainNttCnMo}
	, #{imageAtchIdPc}
	, #{imageAtchIdMo}
	, #{mediaUrlPc}
	, #{mediaUrlMo}
	, #{expsrYn}
	, #{expsrPrty}
	, #{title}
	, #{btYn}
	, #{btSjPc1}
	, #{btSjMo1}
	, #{linkUrl1}
	, #{btSjPc2}
	, #{btSjMo2}
	, #{linkUrl2}
	, now()
	, #{registerSn}
	, now()
	, #{updusrSn}			
)

mapper.update:
update tb_main_ntt
set
	updt_dt = now()
	, updusr_sn = #{updusrSn}
	, lang_code = #{langCode}
	, main_bbs_id = #{mainBbsId}
	, main_ntt_sj_pc = #{mainNttSjPc}
	, main_ntt_sj_Mo = #{mainNttSjMo}
	, main_ntt_cn_Mo = #{mainNttCnPc}
	, main_ntt_cn_Mo = #{mainNttCnMo}
	, image_atch_id_pc = #{imageAtchIdPc}
	, image_atch_id_mo = #{imageAtchIdMo}
	, media_url_pc = #{mediaUrlPc}
	, media_url_mo = #{mediaUrlMo}
	, expsr_yn = #{expsrYn}
	, expsr_prty = #{expsrPrty}
	, title = #{title}
	, bt_yn = #{btYn}
	, bt_sj_pc_1 = #{btSjPc1}
	, bt_sj_mo_1 = #{btSjMo1}
	, link_url_1 = #{linkUrl1}
	, bt_sj_pc_2 = #{btSjPc2}
	, bt_sj_mo_2 = #{btSjMo2}
	, link_url_2 = #{linkUrl2}
where main_ntt_sn = #{mainNttSn}

UPDATE tb_main_ntt SET
	expsr_prty = #{expsrPrty}
WHERE
	main_ntt_sn = #{mainNttSn}
