mapper.insert:
    insert into tb_main_people (
      people_sn
	  , lang_code
	  , people_nm
	  , people_dept
	  , cn_pc
	  , cn_mo
	  , atch_id_pc
	  , atch_id_mo
	  , view_yn
	  , sort_ordr
	  , regist_dt
	  , register_sn
	  , updt_dt
	  , updusr_sn
    ) values (
      #{peopleSn}
      , #{langCode}
      , #{peopleNm}
      , #{peopleDept}
      , #{cnPc}
      , #{cnMo}
      , #{imageAtchIdPc}
      , #{imageAtchIdMo}
      , #{viewYn}
      , #{sortOrdr}
      , now()
      , #{registerSn}
      , now()
      , #{updusrSn}
    )

mapper.update:
update tb_main_people
set
    updt_dt = now()
    , updusr_sn = #{updusrSn}
    , people_nm = #{peopleNm}
    , people_dept = #{peopleDept}
    , cn_pc = #{cnPc}
    , cn_mo = #{cnMo}
    , sort_ordr = #{sortOrdr}
    , view_yn = #{viewYn}
  , atch_id_pc = #{imageAtchIdPc} 
  , atch_id_mo = #{imageAtchIdMo} 
where people_sn = #{peopleSn}

update tb_main_people
set
	sort_ordr = #{sortOrdr}
where people_sn = #{peopleSn}
