mapper.insert:
    insert into tb_main_people (
      people_sn
	  , lang_code
	  , people_nm
	  , people_dept
	  , cn_pc
	  , cn_mo
	  , link_url
	  , atch_id
	  , sort_ordr
	  , regist_dt
	  , register_sn
	  , updt_dt
	  , updusr_sn
    ) values (
      #{peopleSn}
      , #{langCode}
      , #{peopleNm}
      , #{peopleDept}
      , #{cnPc}
      , #{cnMo}
      , #{linkUrl}
      , #{imageAtchId}
      , #{sortOrdr}
      , now()
      , #{registerSn}
      , now()
      , #{updusrSn}
    )

mapper.update:
update tb_main_people
set
    updt_dt = now()
    , updusr_sn = #{updusrSn}
    , people_nm = #{peopleNm}
    , people_dept = #{peopleDept}
    , cn_pc = #{cnPc}
    , cn_mo = #{cnMo}
    , link_url = #{linkUrl}
  , atch_id = #{imageAtchId} 
where people_sn = #{peopleSn}
