mapper.insert:
insert into tb_ntt (
	ntt_sn
	, lang_code
	, bbs_id
	, bbs_ctgry_sn
	, ntt_sj
	, ntt_cn
	, expsr_yn
	, rdcnt
	, upend_fixing_yn
	, wrter_sn
	, regist_dt
	, register_sn
	, updt_dt
	, updusr_sn
	, nmbr_wrter_nm
	, mbtlnum
	, thumb_bass_image_use_yn
	, thumb_atch_id
	, thumb_bass_image_code
	, mvp_url
) values (
	#{nttSn}
	, #{langCode}
	, #{bbsId}
	, #{bbsCtgrySn}
	, #{nttSj}
	, #{nttCn}
    , ifnull(#{expsrYn}, 'Y')
    , 0
	, ifnull(#{upendFixingYn}, 'N')
	, #{wrterSn}
	, now()
	, #{registerSn}
	, now()
	, #{updusrSn}
	, #{nmbrWrterNm, typeHandler=aesCipherTypeHandler}
	, #{mbtlnum, typeHandler=aesCipherTypeHandler}
	, #{thumbBassImageUseYn}
	, #{thumbAtchId}
	, #{thumbBassImageCode}
	, #{mvpUrl}
)

mapper.update:
update tb_ntt
set
    updt_dt = now()
    , updusr_sn = #{updusrSn}
    , lang_code = #{langCode} 
    , bbs_id = #{bbsId} 
    , bbs_ctgry_sn = #{bbsCtgrySn} 
    , ntt_sj = #{nttSj} 
    , ntt_cn = #{nttCn} 
    , expsr_yn = #{expsrYn} 
    , rdcnt = #{rdcnt} 
    , upend_fixing_yn = #{upendFixingYn} 
    , nmbr_wrter_nm = #{nmbrWrterNm} 
    , mbtlnum = #{mbtlnum} 
    , thumb_bass_image_use_yn = #{thumbBassImageUseYn} 
    , thumb_atch_id = #{thumbAtchId} 
    , thumb_bass_image_code = #{thumbBassImageCode} 
    , mvp_url = #{mvpUrl} 
where ntt_sn = #{nttSn}
