mapper.insert:
insert into tb_patent (
	patent_sn
	, lang_code
	, ctgry_code
	, nation_code
	, patent_cn
	, regist_dt
	, register_sn
	, updt_dt
	, updusr_sn
) values (
	#{patentSn}
	, #{langCode}
	, #{ctgryCode}
	, #{nationCode}
	, #{patentCn}
	, now()
	, #{registerSn}
	, now()
	, #{updusrSn}
)

mapper.update:
update tb_patent
set
    updt_dt = now()
    , updusr_sn = #{updusrSn}
	, lang_code = #{langCode} 
	, ctgry_code = #{ctgryCode} 
	, nation_code = #{nationCode} 
	, patent_cn = #{patentCn} 
where patent_sn = #{patentSn}
