mapper.insert:
insert into tb_policy (
	policy_sn
	, lang_code
	, policy_code
	, policy_ver
	, expsr_yn
	, applc_de
	, policy_cn
	, regist_dt
	, register_sn
	, updt_dt
	, updusr_sn
) values (
	#{policySn}
	, #{langCode}
	, #{policyCode}
	, #{policyVer}
	, #{expsrYn}
	, #{applcDe}
	, #{policyCn}
	, now()
	, #{registerSn}
	, now()
	, #{updusrSn}
)

mapper.update:
update tb_policy
set
    updt_dt = now()
    , updusr_sn = #{updusrSn}
	, lang_code = #{langCode} 
	, policy_code = #{policyCode} 
	, policy_ver = #{policyVer} 
	, expsr_yn = #{expsrYn} 
	, applc_de = #{applcDe} 
	, policy_cn = #{policyCn} 
where policy_sn = #{policySn}

mapper.select:
select ifnull(max(policy_ver), 0) 
from tb_policy tp 
where tp.lang_code = #{langCode}
and tp.policy_code = #{policyCode}
