mapper.insert:
INSERT INTO tb_role (
	ROLE_ID, ROLE_NM, REGIST_DT, REGISTER_SN, UPDT_DT, UPDUSR_SN
) VALUES (
	#{roleId}, #{roleNm}, now(), #{registerSn}, now(), #{updusrSn}
)

mapper.update:
UPDATE
	tb_role
SET
	UPDT_DT = now()
	, UPDUSR_SN = #{updusrSn}
	, ROLE_NM = #{roleNm}
WHERE
	ROLE_ID = #{roleId}
