  , updt_dt
  , updusr_sn
  , post_dt
) values (
  #{cntntsSn}
  , #{langCode}
  , #{cntntsCtgryCode}
  , #{imageAtchId}
  , #{cntntsSj}
  , #{sortOrdr}
  , now()
  , #{registerSn}
  , now()
  , #{updusrSn}
  , #{postDt}
)

mapper.update:
update tb_social_contents
set
    updt_dt = now()
    , updusr_sn = #{updusrSn}
  , lang_code = #{langCode} 
  , cntnts_ctgry_code = #{cntntsCtgryCode} 
  , image_atch_id = #{imageAtchId} 
