mapper.insert:
insert into tb_thesis (
	thesis_sn
	, lang_code
	, thesis_sj
	, jrnl
	, link_url
	, regist_year
	, regist_mt
	, regist_dt
	, register_sn
	, updt_dt
	, updusr_sn
) values (
	#{thesisSn}
	, #{langCode}
	, #{thesisSj}
	, #{jrnl}
	, #{linkUrl}
	, #{registYear}
	, #{registMt}
	, now()
	, #{registerSn}
	, now()
	, #{updusrSn}
)

mapper.update:
update tb_thesis
set
    updt_dt = now()
    , updusr_sn = #{updusrSn}
	, lang_code = #{langCode} 
	, thesis_sj = #{thesisSj} 
	, jrnl = #{jrnl} 
	, link_url = #{linkUrl} 
	, regist_year = #{registYear} 
	, regist_mt = #{registMt} 
where thesis_sn = #{thesisSn}
