mapper.insert:
insert into tb_user (
	user_sn
    , user_nm
    , login_id
    , login_password
    , email
    , telno
    , mbtlnum
    , last_password_change_dt
    , password_input_error_co
    , login_co
    , last_login_dt
    , user_se_code
	, password_initl_yn
    , regist_dt, register_sn, updt_dt, updusr_sn
) values (
	#{userSn}
    , #{userNm}
    , #{loginId}
    , #{loginPassword}
    , #{email, typeHandler=aesCipherTypeHandler}
    , #{telno, typeHandler=aesCipherTypeHandler}
    , #{mbtlnum, typeHandler=aesCipherTypeHandler}
    , now()
    , 0
    , 0
    , null
    , #{userSeCode}
    , passwordInitlYn
    , now(), #{registerSn}, now(), #{updusrSn}
)

mapper.update:
update
	tb_user
set
    updt_dt = now()
    , updusr_sn = #{updusrSn}
	 , user_nm = #{userNm} 
	 , email = #{email, typeHandler=aesCipherTypeHandler} 
	 , telno = #{telno, typeHandler=aesCipherTypeHandler} 
	 , mbtlnum = #{mbtlnum, typeHandler=aesCipherTypeHandler} 
	 , user_se_code = #{userSeCode} 
	 , password_initl_yn = #{passwordInitlYn} 

	, login_password = #{loginPassword}
	, last_password_change_dt = now()

where
	user_sn = #{userSn}

update
	tb_user
set
	password_initl_yn = #{passwordInitlYn}
where
	user_sn = #{userSn}
