	, updusr_sn
) values (
	#{wnpzSn}
	, #{langCode}
	, #{wnpzClCode}
	, #{wnpzTmeCode}
	, #{photoAtchId}
	, #{wnpzNm}
	, #{occp}
	, #{rm}
	, #{wnpzCn}
	, now()
	, #{registerSn}
	, now()
	, #{updusrSn}
)

mapper.update:
